mapper.select:
select
  id, account, password, status, role_id, roles
from rbac_manager


        and id = #{id}


        and account = #{account}


        and password = #{password}


        and status = #{status}


        and role_id = #{roleId}


        and roles = #{roles}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from rbac_manager


        and id = #{id}


        and account = #{account}


        and password = #{password}


        and status = #{status}


        and role_id = #{roleId}


        and roles = #{roles}
