mapper.select:
select
  *
from household


        and id = #{houseHold.id}


        and name = #{houseHold.name}


        and age = #{houseHold.age}


        and gender = #{houseHold.gender}


        and telephone = #{houseHold.telephone}


        and house_id = #{houseHold.house_id}


        and is_owner = #{houseHold.is_owner}


        and state = #{houseHold.state}

mapper.update:
update household


            and name = #{houseHold.name}


            and age = #{houseHold.age}


            and gender = #{houseHold.gender}


            and telephone = #{houseHold.telephone}


            and house_id = #{houseHold.house_id}


            and is_owner = #{houseHold.is_owner}


            and state = #{houseHold.state}


where id = #{houseHold.id}

mapper.select:
select count(*)
from household


        and id = #{houseHold.id}


        and name = #{houseHold.name}


        and age = #{houseHold.age}


        and gender = #{houseHold.gender}


        and telephone = #{houseHold.telephone}


        and house_id = #{houseHold.house_id}


        and is_owner = #{houseHold.is_owner}


        and state = #{houseHold.state}
